import re
def clean_desc(description: str) -> dict:
    # extract description and requirements from the job description
    about_role = re.search(r"About the Role:\s*(.*?)\s*What You['‘]ll Do:", description, re.DOTALL)
    if about_role:
        desc = about_role.group(1).strip()
        # remove any new line characters
        desc = str(re.sub(r"\n", " ", desc))
    else:
        desc = ""
    requirement = re.search(r"What You['’]ll Need:\s*(.*?)\s*Benefits of Working at CrowdStrike:", description, re.DOTALL)
    if requirement:
        req = requirement.group(1).strip()
    else:
        req = ""
    # appened req as a list to requirements
    requirements = []
    for r in req.split("\n"):
        if r.strip() and not r.strip() == "Bonus Points:":
            requirements.append(r.strip())
    # extract job deadline
    deadline = re.search(r"Expected Close Date of Job Posting is:(.*)", description)
    if deadline:
        ddl = str(deadline.group(1).strip())
    else:
        ddl = ""
    return { "description": desc, "requirements": requirements, "deadline": ddl }

